import React from 'react';
import './App.css';

class Clock extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			date: new Date().toLocaleTimeString(),
		}
	}

	componentDidMount() {
		setInterval(() => {
			this.tick();
		}, 1000);
	}

	componentWillUnmount() {
	}

	tick = () => {
		this.setState({
			date: new Date().toLocaleTimeString()
		});
	};

	render() {
		return (
			<div>
				<h1>date is {this.state.date}</h1>
			</div>
		)
	}
}

function App() {
	return (
		<Clock />
	);
}

export default App;
