import React from 'react';
import './App.css';

class Clock extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			date: new Date().toLocaleTimeString(),
			food: ['Pizza', 'Burger', 'Falooda', 'Ice-Cream'],
			isToggleOn: true,
		}
	}

	componentDidMount() {
		setInterval(() => {
			this.tick();
		}, 1000);
	}

	componentWillUnmount() {
		clearInterval();
	}

	// tick function that set the date in the state.
	tick = () => {
		this.setState({
			date: new Date().toLocaleTimeString()
		});
	};

	handleClick=()=> {
		this.setState((state, props) => ({
			isToggleOn: !state.isToggleOn
		}));
	};

	render() {
		return (
			<div>
				<h1>date is {this.state.date}</h1>
				<button type='button' onClick={()=>this.setState({isToggleOn:!this.state.isToggleOn})}>Toggle {this.state.isToggleOn ? 'On' : 'Off'} </button>
			</div>
		)
	}
}

function App() {
	return (
		<Clock/>
	);
}

export default App;
